mapper.select:
select *
from chain_dividend_his
where `action` = #{action.code,jdbcType=TINYINT}
  and status = #{status.code,jdbcType=TINYINT}

mapper.update:

update ac_chain_dividend_his
set update_time = ROUND(UNIX_TIMESTAMP(NOW()) * 1000),
    version     = version + 1,
    status      = #{status.code,jdbcType=TINYINT}
where id = #{id,jdbcType=BIGINT}
  and status <> #{status.code,jdbcType=TINYINT}

mapper.select:
select *
from ac_chain_dividend_his
where tx_hash = #{txHash,jdbcType=VARCHAR}
  and chain = #{chain.code,jdbcType=TINYINT}


select *
from ac_chain_dividend_his
where status = #{status.code,jdbcType=TINYINT}
  and chain = #{chain.code,jdbcType=TINYINT}
  and update_time <= #{timestamp,jdbcType=BIGINT}

select *
from ac_chain_dividend_his
where chain = #{chain.code,jdbcType=TINYINT}
  and status = #{status.code,jdbcType=TINYINT}
