mapper.update:
update user_account_action_his
set update_time = ROUND(UNIX_TIMESTAMP(NOW()) * 1000),
    version = version + 1,
    status      = #{status.code,jdbcType=TINYINT}
where `action`    = #{action.code,jdbcType=SMALLINT}
  and `source`     = #{source,jdbcType=VARCHAR}
  and `user_id`    = #{userId,jdbcType=BIGINT}

mapper.select:
select *
from user_account_action_his
where user_id = #{userId,jdbcType=BIGINT}
  and create_time >= #{startTimestamp,jdbcType=BIGINT} 
  and create_time < #{endTimestamp,jdbcType=BIGINT} 
  and currency = #{currency,jdbcType=VARCHAR} 
  and `action` = #{action.code,jdbcType=TINYINT}
