mapper.update:

update user_account
set update_time = ROUND(UNIX_TIMESTAMP(NOW()) * 1000),
    version     = version + 1,
    available   = available - #{amount,jdbcType=DECIMAL},
    freeze      = freeze + #{amount,jdbcType=DECIMAL}
where user_id = #{userId,jdbcType=BIGINT}
  and currency = #{currency,jdbcType=VARCHAR}
  and available >= #{amount,jdbcType=DECIMAL}

update user_account
set update_time = ROUND(UNIX_TIMESTAMP(NOW()) * 1000),
version = version + 1,
available = available + #{amount,jdbcType=DECIMAL}
where user_id = #{userId,jdbcType=BIGINT}
and currency = #{currency,jdbcType=VARCHAR}
and available + #{amount,jdbcType=DECIMAL} >= 0

update t_user_account
set f_update_time = ROUND(UNIX_TIMESTAMP(NOW()) * 1000),
f_version = f_version + 1,
f_freeze = f_freeze + #{amount,jdbcType=DECIMAL}
where f_user_id = #{userId,jdbcType=BIGINT}
and f_currency = #{currency,jdbcType=VARCHAR}
and f_freeze + #{amount,jdbcType=DECIMAL} >= 0


update user_account
set update_time = ROUND(UNIX_TIMESTAMP(NOW()) * 1000),
    version     = version + 1,
    available   = available + #{amount,jdbcType=DECIMAL},
    freeze      = freeze - #{amount,jdbcType=DECIMAL}
where user_id = #{userId,jdbcType=BIGINT}
  and currency = #{currency,jdbcType=VARCHAR}
  and freeze >= #{amount,jdbcType=DECIMAL}

mapper.select:
select count(*)
from user_account
where user_id = #{userId,jdbcType=BIGINT}
  and currency = #{currency,jdbcType=VARCHAR}
