mapper.select:
select t4.id, t1.price, t1.place_time, t1.token_id, t1.auction_id, t4.name, t2.image, t3.rarity, t3.victory, t3.lose, t3.max_streak, t3.capture, t3.killing_spree, t3.goblin_kill, t3.slaying, t3.goblin
from z_market_order t1 join z_equipment_nft t4 on t4.mint_address = t1.mint_address left join ga_nft_public_backpack t2 on t4.id = t2.eq_nft_id left join ga_nft_attribute t3 on t4.id = t3.eq_nft_id where t2.type = 3 AND t1.status = 1
and CONCAT(t4.name,t1.token_id) LIKE CONCAT('%', #{params.name}, '%')
and t1.auction_id = #{params.auctionId}
and t3.rarity = #{params.rarity}
and t3.hero_type = #{params.heroType}
and t1.price >= ${params.minPrice}
and t1.price <= ${params.maxPrice}
order by t1.price ${params.sortType}
order by t3.rarity ${params.sortType}
order by t1.place_time ${params.sortType}

select
t4.id,
t1.place_time,
t1.price,
t1.token_id,
t1.auction_id,
t4.name,
t2.image,
t3.durability,
t3.grade,
t3.health,
t3.armor,
t3.vs_monster,
t3.passive,
t3.blade_buff,
t3.rarity_attr_value,
t3.base_attr_value

from z_market_order t1
join z_equipment_nft t4 on t4.mint_address  = t1.mint_address
left join ga_nft_public_backpack t2 on t4.id = t2.eq_nft_id
left join ga_nft_attribute t3 on t4.id = t3.eq_nft_id
where t2.type = 1 AND t1.status = 1


    and CONCAT(t4.name,t1.token_id) LIKE CONCAT('%', #{params.name}, '%')


    and t1.auction_id  = #{params.auctionId}


    and t3.grade = #{params.grade}


    and t3.durability >= ${params.minDurability}


    and t3.durability <= ${params.maxDurability}


    and t1.price >= ${params.minPrice}


    and t1.price <= ${params.maxPrice}


    order by t1.price ${params.sortType}


    order by t3.grade ${params.sortType}


    order by t3.durability ${params.sortType}


    order by t1.place_time ${params.sortType}

select
t4.id,
t1.price,
t1.place_time,
t1.token_id,
t1.auction_id,
t4.name,
t2.image,
t3.grade,
t3.durability,
t3.health,
t3.armor,
t3.vs_monster,
t3.passive,
t3.blade_buff,
t3.rarity_attr_value,
t3.base_attr_value

from z_market_order t1
join z_equipment_nft t4 on t4.mint_address  = t1.mint_address
left join ga_nft_public_backpack t2 on t4.id = t2.eq_nft_id
left join ga_nft_attribute t3 on t4.id = t3.eq_nft_id
where t2.type = 2 AND t1.status = 1


    and CONCAT(t4.name,t1.token_id) LIKE CONCAT('%', #{params.name}, '%')


    and t1.auction_id  = #{params.auctionId}


    and t3.grade = #{params.grade}


    and t3.durability >= ${params.minDurability}


    and t3.durability <= ${params.maxDurability}


    and t1.price >= ${params.minPrice}


    and t1.price <= ${params.maxPrice}


    order by t1.price ${params.sortType}


    order by t3.grade ${params.sortType}


    order by t3.durability ${params.sortType}


    order by t1.place_time ${params.sortType}
