mapper.select:
select t1.name, t1.grade, t1.image, t1.token_id, t3.price, t1.state, t1.auto_id, t1.eq_nft_id, t3.place_time as listTime, t1.server_role_id, t2.durability, t2.durability_config, t2.base_attr_value as baseAttr, t2.rarity_attr_value as rarityAttr, t2.special_attr_desc, t2.passive_attr_desc from ga_nft_public_backpack t1 left join ga_nft_attribute t2 on t1.eq_nft_id = t2.eq_nft_id left join (select price, place_time, mint_address from z_market_order where status = 1) t3 on t1.token_address = t3.mint_address where t1.user_id = #{userId} and t1.state <> 1
and t1.state = #{state}
and t1.type = #{type}
and t1.item_type_id = #{itemTypeId}
and t1.server_role_id = #{serverRoleId} and state = 3
order by t3.price ${sortTypeStr}
order by t1.grade ${sortTypeStr}
order by t2.durability ${sortTypeStr}
order by t2.rarity ${sortTypeStr}
order by t3.place_time ${sortTypeStr}

select t1.name, t1.image, t1.image_sha, t1.token_id, t3.price, t1.state, t1.eq_nft_id, t3.place_time as listTime, t1.server_role_id, t2.victory, t2.lose, t2.max_streak, t2.capture, t2.killing_spree, t2.goblin_kill, t2.slaying, t2.goblin from ga_nft_public_backpack t1 left join ga_nft_attribute t2 on t1.eq_nft_id = t2.eq_nft_id left join (select price, place_time, mint_address from z_market_order where status = 1) t3 on t1.token_address = t3.mint_address where t1.type =3 and t1.user_id = #{userId} and t1.state <> 1
and t1.name = #{skin}
and t1.state = #{state}
and t2.hero_type = #{heroType}
order by t3.price ${sortTypeStr}
order by t2.rarity ${sortTypeStr}
order by t3.place_time ${sortTypeStr}
