mapper.update:
update t_hot_wallet
set f_chain = #{chain,jdbcType=TINYINT},
    f_address = #{address,jdbcType=VARCHAR},
    f_file_json = #{fileJson,jdbcType=VARCHAR},
    f_created_at = #{createdAt,jdbcType=BIGINT}
where f_id = #{id,jdbcType=BIGINT}

mapper.select:
select *
from t_hot_wallet
where f_chain = #{chain,jdbcType=TINYINT} and f_address = #{address,jdbcType=VARCHAR}
