mapper.update:
update hot_wallet
set chain = #{chain,jdbcType=TINYINT},
    address = #{address,jdbcType=VARCHAR},
    file_json = #{fileJson,jdbcType=VARCHAR},
    created_at = #{createdAt,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select *
from hot_wallet
where chain = #{chain,jdbcType=TINYINT} and address = #{address,jdbcType=VARCHAR}
